<?phpnamespace Differ;function plain(array $diff, int $depth = 0, string $lf = "\n"): string{    return array_reduce(        $diff,        function ($str, $arr) use ($lf, $depth) {            $translate = [                '+' => "added with value:{$arr['val']}",                '-' => 'removed',            ];//            $offset = str_repeat('    ', $depth);            if (is_array($arr['val'])) {                $depth++;                $str .= plain($arr['val'], $depth);                $str .= "$lf";            } else {                if ($arr['z'] === ' ') {                    return '';                }                $str .= "Property '{$arr['key']}' was {$translate[$arr['z']]}$lf";            }            return $str;        },        ""    );    /*    return array_reduce(        $diff,        function ($str, $arr) use ($lf, $depth) {            $offset = str_repeat('    ', $depth);            if (is_array($arr['val'])) {                $depth++;                $str .= stylish($arr['val'], $depth);                $str .= "$offset    }$lf";            } else {                $str .= "$offset  {$arr['z']} {$arr['key']}: {$arr['val']}$lf";            }            return $str;        },        ""    );    */}